import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { Product } from '../models/Product';
import { productService } from '../services/productService';

const ProductDetails: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [product, setProduct] = useState<Product | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        if (id) {
          const data = await productService.getProductById(id);
          setProduct(data);
        }
        setLoading(false);
      } catch (err) {
        setError('Error al cargar el producto');
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleDelete = async () => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este producto?')) {
      try {
        if (id) {
          await productService.deleteProduct(id);
          navigate('/');
        }
      } catch (err) {
        setError('Error al eliminar el producto');
      }
    }
  };

  if (loading) return <div className="text-center p-4">Cargando...</div>;
  if (error) return <div className="text-center text-red-500 p-4">{error}</div>;
  if (!product) return <div className="text-center p-4">Producto no encontrado</div>;

  return (
    <div className="container mx-auto p-4 max-w-2xl">
      <h1 className="text-2xl font-bold mb-4">{product.name}</h1>
      <div className="bg-white shadow-md rounded p-6 mb-4">
        <div className="mb-4">
          <span className="text-gray-700 font-semibold">Categoría:</span>{' '}
          {product.category}
        </div>
        <div className="mb-4">
          <span className="text-gray-700 font-semibold">Descripción:</span>
          <p className="mt-1">{product.description}</p>
        </div>
        <div className="mb-4">
          <span className="text-gray-700 font-semibold">Precio:</span>{' '}
          ${product.price.toFixed(2)}
        </div>
        <div className="mb-4">
          <span className="text-gray-700 font-semibold">Stock:</span>{' '}
          {product.stock} unidades
        </div>
        {product.createdAt && (
          <div className="text-sm text-gray-500 mb-1">
            Creado: {new Date(product.createdAt).toLocaleDateString()}
          </div>
        )}
        {product.updatedAt && (
          <div className="text-sm text-gray-500">
            Última actualización: {new Date(product.updatedAt).toLocaleDateString()}
          </div>
        )}
      </div>
      <div className="flex gap-2">
        <Link
          to="/"
          className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded"
        >
          Volver
        </Link>
        <Link
          to={`/products/edit/${product._id}`}
          className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded"
        >
          Editar
        </Link>
        <button
          onClick={handleDelete}
          className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded"
        >
          Eliminar
        </button>
      </div>
    </div>
  );
};

export default ProductDetails;
