import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Register: React.FC = () => {
  const [name, setName] = useState<string>('');
  const [email, setEmail] = useState<string>('');
  const [password, setPassword] = useState<string>('');
  const [confirmPassword, setConfirmPassword] = useState<string>('');
  const [cedula, setCedula] = useState<string>('');
  const [formError, setFormError] = useState<string | null>(null);
  
  const { register, loading, error, clearError } = useAuth();
  
  useEffect(() => {
    return () => {
      clearError();
    };
  }, [clearError]);
  
  const validateCedula = (cedula: string): boolean => {
    // Verificar que tenga 10 dígitos
    if (!/^\d{10}$/.test(cedula)) {
      return false;
    }
    
    // Algoritmo de verificación de cédula ecuatoriana
    const digitoVerificador = parseInt(cedula.substring(9, 10));
    const coeficientes = [2, 1, 2, 1, 2, 1, 2, 1, 2];
    let suma = 0;
    
    for (let i = 0; i < coeficientes.length; i++) {
      let valor = parseInt(cedula.charAt(i)) * coeficientes[i];
      if (valor > 9) valor -= 9;
      suma += valor;
    }
    
    const digitoCalculado = suma % 10 === 0 ? 0 : 10 - (suma % 10);
    return digitoCalculado === digitoVerificador;
  };
  
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setFormError(null);
    
    // Validación de contraseñas
    if (password !== confirmPassword) {
      setFormError('Las contraseñas no coinciden');
      return;
    }
    
    // Validación de cédula en el frontend
    if (!validateCedula(cedula)) {
      setFormError('La cédula ingresada no es válida');
      return;
    }
    
    await register(name, email, password, cedula);
  };
  
  return (
    <div className="container mx-auto p-4">
      <div className="max-w-md mx-auto bg-white p-8 border rounded shadow-md">
        <h1 className="text-2xl font-bold mb-6 text-center">Registro</h1>
        
        {(error || formError) && (
          <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-4">
            <p>{formError || error}</p>
          </div>
        )}
        
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2" htmlFor="name">
              Nombre Completo
            </label>
            <input
              id="name"
              type="text"
              className="w-full px-3 py-2 border border-gray-300 rounded"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          
          <div className="mb-4">
            <label className="block text-gray-700 mb-2" htmlFor="email">
              Email
            </label>
            <input
              id="email"
              type="email"
              className="w-full px-3 py-2 border border-gray-300 rounded"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          
          <div className="mb-4">
            <label className="block text-gray-700 mb-2" htmlFor="cedula">
              Cédula
            </label>
            <input
              id="cedula"
              type="text"
              className="w-full px-3 py-2 border border-gray-300 rounded"
              value={cedula}
              onChange={(e) => setCedula(e.target.value)}
              maxLength={10}
              required
            />
          </div>
          
          <div className="mb-4">
            <label className="block text-gray-700 mb-2" htmlFor="password">
              Contraseña
            </label>
            <input
              id="password"
              type="password"
              className="w-full px-3 py-2 border border-gray-300 rounded"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              minLength={6}
              required
            />
          </div>
          
          <div className="mb-6">
            <label className="block text-gray-700 mb-2" htmlFor="confirmPassword">
              Confirmar Contraseña
            </label>
            <input
              id="confirmPassword"
              type="password"
              className="w-full px-3 py-2 border border-gray-300 rounded"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
          </div>
          
          <div className="flex items-center justify-between mb-4">
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
              disabled={loading}
            >
              {loading ? 'Cargando...' : 'Registrarse'}
            </button>
          </div>
          
          <div className="text-center">
            <p>
              ¿Ya tienes una cuenta?{' '}
              <Link to="/login" className="text-blue-500 hover:underline">
                Inicia Sesión
              </Link>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Register;
